import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";


export default function NewsCard(props) {
	//Extrag props-urile componentei
	const { newsId, imgSrc, title, description } = props;
	const [showAlert, setShowAlert] = useState(false);

	const handleAddToFavorites = () => {
		setShowAlert(true);
		setTimeout(() => {
			setShowAlert(false);
		}, 2000);
	};

	return (
		// La click pe card, vom deschide pagina cu detalii pentru stire
		<Card className="newsCard d-flex flex-column align-items-center justify-content-between h-100">
            {/* Caracterul / din id il deruteaza pe React Router, asa ca o sa codificam id-ul*/}
			<Link to={`/news/${encodeURIComponent(newsId)}`}>
				<Card.Img src={imgSrc} variant="top" />
				<Card.Body>
					<Card.Title>{title}</Card.Title>
					<Card.Text>{description}</Card.Text>
				</Card.Body>
			</Link>
			<Button variant="success" onClick={handleAddToFavorites} className="width-50">Adauga la favorite</Button>
			{showAlert && (
				<div className="alert alert-success fixed-top w-100 text-center" role="alert">
					Produsul a fost adaugat la favorite
				</div>
			)}
		</Card>
	);
}
